// src/components/CartPage.jsx
import React, { useContext, useState } from "react";
import { CartContext } from "../context/CartContext";
// import { placeOrder } from "../api";
import axios from "axios";
import { toast, ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

const CartPage = () => {
  // const { cart, updateQuantity, clearCart } = useContext(CartContext);
 // const [form, setForm] = useState({ firstName: "", lastName: "", address: "" });
 const [message, setMessage] = useState("");

  
  // ✅ match backend field names

  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [address, setAddress] = useState("");
  const { cart, clearCart } = useContext(CartContext);
  const [name, setName] = useState("");
  
  const [city, setCity] = useState("");
  const [successMessage, setSuccessMessage] = useState("");
  const [isError, setIsError] = useState(false); // ✅ track error or success








  // const handleChange = (e) => {
  //   setForm({ ...form, [e.target.name]: e.target.value });
  // };

  // const placeOrder = () => {
  //   if (!form.firstName || !form.lastName || !form.address) {
  //     setMessage("Please fill all required fields.");
  //     return;
  //   }

  //   // later will call backend API
  //   console.log("Order Placed:", { cart, form });
  //   setMessage("✅ Order placed successfully!");
  //   clearCart();
  // };



  // const placeOrderHandler = async () => {
  //   if (!form.firstName || !form.lastName || !form.address) {
  //     setMessage("Please fill all required fields.");
  //     return;
  //   }
  
  //   try {
  //     const res = await placeOrder({ ...form, cart });
  //     if (res.message) {
  //       setMessage("✅ " + res.message);
  //       clearCart();
  //       // reset form
  //       setForm({ firstName: "", lastName: "", address: "" });
  //     } else {
  //       setMessage("❌ Something went wrong.");
  //     }
  //   } catch (err) {
  //     console.error(err);
  //     setMessage("❌ Failed to place order.");
  //   }
  // };
  const total = cart.reduce((acc, item) => acc + item.price * item.quantity, 0);

  const handlePlaceOrder = async () => {
    if (!firstName || !lastName || !address) {
      toast.error("❌ Please fill all required fields.");
      return;
    }
    
    try {
      const orderData = {
        firstName,
        lastName,
        address,
        cart,
      };

      const response = await axios.post("http://localhost:5000/api/order", orderData);

       // ✅ show backend success msg
      // ✅ show success message
      // ✅ success toast
      toast.success("✅ " + response.data.message);

  // clear cart & reset form

      clearCart(); // ✅ empty cart after placing order
      setFirstName("");
      setLastName("");
      setAddress("");
    } catch (error) {
      console.error("Order failed", error.response?.data || error.message);
      toast.error(error.response?.data?.message || "❌ Failed to place order. Please try again.");
    }
  };



  return (
    // <div className="p-6">
    //   <h2 className="text-2xl font-bold mb-4">Your Cart</h2>

    //   {cart.length === 0 ? (
    //     <p className="text-green-600 font-semibold">Cart is empty.</p>
    //   ) : (
    //     <>
    //       <ul className="mb-4">
    //         {cart.map((item, index) => (
    //           <li key={index} className="flex justify-between mb-2">
    //             {item.name} - ₹{item.price} × {item.quantity}
    //           </li>
    //         ))}
    //       </ul>

    //       <h3 className="text-lg font-semibold mb-4">Total: ₹{total}</h3>

    //       {/* ✅ Form fields */}
    //       <input
    //         type="text"
    //         placeholder="First Name"
    //         value={firstName}
    //         onChange={(e) => setFirstName(e.target.value)}
    //         className="border p-2 w-full mb-2"
    //         required
    //       />
    //       <input
    //         type="text"
    //         placeholder="Last Name"
    //         value={lastName}
    //         onChange={(e) => setLastName(e.target.value)}
    //         className="border p-2 w-full mb-2"
    //         required
    //       />
    //       <input
    //         type="text"
    //         placeholder="Address"
    //         value={address}
    //         onChange={(e) => setAddress(e.target.value)}
    //         className="border p-2 w-full mb-2"
    //         required
    //       />

    //       <button
    //         onClick={handlePlaceOrder}
    //         className="bg-purple-600 text-white px-4 py-2 rounded"
    //       >
    //         Place Order
    //       </button>
    //     </>
    //   )}

    <div className="cart-container">
  <h2>My Cart</h2>

  {/* {cart.length === 0 ? (
    <p className="empty-cart">Cart is empty</p>
  ) : (
    <> */}
      {/* <div className="cart-items">
        {cart.map((item, index) => (
          <div key={index} className="cart-item">
            <span>{item.name}</span>
            <span>₹{item.price} × {item.quantity}</span>
          </div>
        ))}
      </div> */}

      <div className="cart-items">
        {cart.map((item, index) => (
          <div key={index} className="cart-item">
            <div className="cart-item-left">
              <img src={item.image} alt={item.name} className="cart-item-img" />
              <span>{item.name}</span>
            </div>
            <span>₹{item.price} × {item.quantity}</span>
          </div>
        ))}
      </div>

      <div className="cart-total">
        Total: <strong>₹{total}</strong>
      </div>

      <div className="cart-form">
        <input
          type="text"
          placeholder="First Name"
          value={firstName}
          onChange={(e) => setFirstName(e.target.value)}
        />
        <input
          type="text"
          placeholder="Last Name"
          value={lastName}
          onChange={(e) => setLastName(e.target.value)}
        />
        <input
          type="text"
          placeholder="Address"
          value={address}
          onChange={(e) => setAddress(e.target.value)}
        />
        <button onClick={handlePlaceOrder}>Place Order</button>
      </div>
    {/* </> */}
  )}

      {/* ✅ Toast container */}
      <ToastContainer position="top-right" autoClose={3000} />
    </div>
  );
};
export default CartPage;
