// src/components/CartPage.jsx
import React, { useContext, useState } from "react";
import { CartContext } from "../context/CartContext";
// import { placeOrder } from "../api";

import axios from "axios";
import { toast, ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

const CartPage = () => {
  // const { cart, updateQuantity, clearCart } = useContext(CartContext);
 // const [form, setForm] = useState({ firstName: "", lastName: "", address: "" });
 const [message, setMessage] = useState("");



  
  // ‚úÖ match backend field names

  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [address, setAddress] = useState("");
  const { cart, increaseQuantity, decreaseQuantity, clearCart } = useContext(CartContext);
  const [name, setName] = useState("");
  
  const [city, setCity] = useState("");
  const [successMessage, setSuccessMessage] = useState("");
  const [isError, setIsError] = useState(false); // ‚úÖ track error or success








  // const handleChange = (e) => {
  //   setForm({ ...form, [e.target.name]: e.target.value });
  // };

  // const placeOrder = () => {
  //   if (!form.firstName || !form.lastName || !form.address) {
  //     setMessage("Please fill all required fields.");
  //     return;
  //   }

  //   // later will call backend API
  //   console.log("Order Placed:", { cart, form });
  //   setMessage("‚úÖ Order placed successfully!");
  //   clearCart();
  // };



  // const placeOrderHandler = async () => {
  //   if (!form.firstName || !form.lastName || !form.address) {
  //     setMessage("Please fill all required fields.");
  //     return;
  //   }
  
  //   try {
  //     const res = await placeOrder({ ...form, cart });
  //     if (res.message) {
  //       setMessage("‚úÖ " + res.message);
  //       clearCart();
  //       // reset form
  //       setForm({ firstName: "", lastName: "", address: "" });
  //     } else {
  //       setMessage("‚ùå Something went wrong.");
  //     }
  //   } catch (err) {
  //     console.error(err);
  //     setMessage("‚ùå Failed to place order.");
  //   }
  // };
  const total = cart.reduce((acc, item) => acc + item.price * item.quantity, 0);

  const handlePlaceOrder = async () => {
    if (!firstName || !lastName || !address) {
      toast.error("‚ùå Please fill all required fields.");
      return;
    }
    
    try {
      const orderData = {
        firstName,
        lastName,
        address,
        cart,
      };

      const response = await axios.post("http://localhost:5000/api/order", orderData);

       // ‚úÖ show backend success msg
      // ‚úÖ show success message
      // ‚úÖ success toast
      toast.success("‚úÖ " + response.data.message);

  // clear cart & reset form

      clearCart(); // ‚úÖ empty cart after placing order
      setFirstName("");
      setLastName("");
      setAddress("");
    } catch (error) {
      console.error("Order failed", error.response?.data || error.message);
      toast.error(error.response?.data?.message || "‚ùå Failed to place order. Please try again.");
    }
  };



  return (
    <div className="cart-page-container">
  <h2>üõíMy Cart</h2>
  
    <div className="cart-items">
      {cart.map((item, index) => (
        <div key={index} className="cart-item">
        <div className="cart-item-left">
              <img src={item.image} alt={item.name} className="cart-item-img" />
              <span>{item.name}</span>
            </div>
          <span>{item.name}</span>
          <div className="quantity-controls">
            <button onClick={() => decreaseQuantity(item)}>-</button>
            <span>{item.quantity}</span>
            <button onClick={() => increaseQuantity(item)}>+</button>
          </div>
          <span>‚Çπ{item.price * item.quantity}</span>
        </div>
      ))}
    </div>
  
    <div className="cart-total">Total: ‚Çπ{total}</div>
  
    <form className="cart-form" onSubmit={handlePlaceOrder}>
      <input type="text" placeholder="First Name" required />
      <input type="text" placeholder="Last Name" required />
      <input type="text" placeholder="Address" required />
      <button type="submit">Place Order</button>
    </form>
 

      {/* ‚úÖ Toast container */}
      <ToastContainer position="top-right" autoClose={3000} />
    </div>
  );
};
export default CartPage;
