// src/components/CartPage.jsx
import React, { useContext, useState } from "react";
import { CartContext } from "../context/CartContext";
// import { placeOrder } from "../api";
import axios from "axios";


const CartPage = () => {
  // const { cart, updateQuantity, clearCart } = useContext(CartContext);
 // const [form, setForm] = useState({ firstName: "", lastName: "", address: "" });
  // const [message, setMessage] = useState("");

  
  // ✅ match backend field names

  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [address, setAddress] = useState("");
  const { cart, clearCart } = useContext(CartContext);
  const [name, setName] = useState("");
  
  const [city, setCity] = useState("");
  const [successMessage, setSuccessMessage] = useState("");









  // const handleChange = (e) => {
  //   setForm({ ...form, [e.target.name]: e.target.value });
  // };

  // const placeOrder = () => {
  //   if (!form.firstName || !form.lastName || !form.address) {
  //     setMessage("Please fill all required fields.");
  //     return;
  //   }

  //   // later will call backend API
  //   console.log("Order Placed:", { cart, form });
  //   setMessage("✅ Order placed successfully!");
  //   clearCart();
  // };



  // const placeOrderHandler = async () => {
  //   if (!form.firstName || !form.lastName || !form.address) {
  //     setMessage("Please fill all required fields.");
  //     return;
  //   }
  
  //   try {
  //     const res = await placeOrder({ ...form, cart });
  //     if (res.message) {
  //       setMessage("✅ " + res.message);
  //       clearCart();
  //       // reset form
  //       setForm({ firstName: "", lastName: "", address: "" });
  //     } else {
  //       setMessage("❌ Something went wrong.");
  //     }
  //   } catch (err) {
  //     console.error(err);
  //     setMessage("❌ Failed to place order.");
  //   }
  // };
  

  const handlePlaceOrder = async () => {
    try {
      const orderData = {
        firstName,
        lastName,
        address,
        cart,
      };

      const response = await axios.post("http://localhost:5000/api/order", orderData);

      alert(response.data.message); // ✅ show backend success msg
      clearCart(); // ✅ empty cart after placing order
      setFirstName("");
      setLastName("");
      setAddress("");
    } catch (error) {
      console.error("Order failed", error.response?.data || error.message);
      alert(error.response?.data?.message || "Failed to place order. Please try again.");
    }
  };




  return (
    <div className="cart">
      <h2>Your Cart</h2>
      {cart.length === 0 ? (
        <p style={{ color: "green" }}>
          {successMessage ? successMessage : "Cart is empty."}
        </p>
      ) : (
        <>
          {cart.map((item, index) => (
            <div key={index}>
              {item.name} - ₹{item.price}
            </div>
          ))}
          <input
            placeholder="Name"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
          <input
            placeholder="Address"
            value={address}
            onChange={(e) => setAddress(e.target.value)}
          />
          <input
            placeholder="City"
            value={city}
            onChange={(e) => setCity(e.target.value)}
          />
          <button onClick={handlePlaceOrder}>Place Order</button>
        </>
      )}
    </div>
  );
};

export default CartPage;
