// src/components/ProductCard.jsx
import React, { useContext } from "react";
import { CartContext } from "../context/CartContext";

const ProductCard = ({ product }) => {
  const { addToCart } = useContext(CartContext);

//   return (
//     <div className="card">
//       <img src={product.image} alt={product.name} className="card-img" />
//       <h3>{product.name}</h3>
//       <p>{product.description}</p>
//       <p>₹{product.price}</p>
//       <button onClick={() => addToCart(product)}>Add to Cart</button>
//     </div>
//   );
// };

// export default ProductCard;




return (
  <div className="border rounded-lg shadow hover:shadow-lg transition p-4 flex flex-col">
    {/* Product Image */}
    <img
      src={product.image}
      alt={product.name}
      className="w-full h-48 object-cover rounded mb-4"
    />

    {/* Product Details */}
    <h3 className="text-lg font-semibold mb-2">{product.name}</h3>
    <p className="text-gray-600 text-sm mb-2">{product.description}</p>
    <p className="text-purple-600 font-bold mb-4">₹{product.price}</p>

    {/* Add to Cart Button */}
    <button
      onClick={() => addToCart(product)}
      className="mt-auto bg-purple-600 text-white px-4 py-2 rounded hover:bg-purple-700 transition"
    >
      Add to Cart
    </button>
  </div>
);
};

export default ProductCard;
