import React, { createContext, useState } from "react";

export const CartContext = createContext();

const CartProvider = ({ children }) => {
  const [cart, setCart] = useState([]);

  
  // ✅ Add to Cart

  const addToCart = (product) => {
    setCart((prevCart) => {
      const existing = prevCart.find((item) => item.id === product.id);
      if (existing) {
        return prevCart.map((item) =>
          item.id === product.id ? { ...item, quantity: item.quantity + 1 } : item
        );
      }
      return [...prevCart, { ...product, quantity: 1 }];
    });
  };

  // ✅ Update quantity (remove item if quantity < 1)
  // const updateQuantity = (id, quantity) => {
  //   setCart((prevCart) =>
  //     prevCart.map((item) =>
  //       item.id === id ? { ...item, quantity: Number(quantity) } : item
  //     )
  //   );
  // };

  const updateQuantity = (id, quantity) => {
    setCart((prevCart) => {
      if (quantity < 1) {
        // remove product if qty is 0
        return prevCart.filter((item) => item.id !== id);
      }
      return prevCart.map((item) =>
        item.id === id ? { ...item, quantity: Number(quantity) } : item
      );
    });
  };

  const increaseQuantity = (product) => {
    setCart((prevCart) =>
      prevCart.map((item) =>
        item.id === product.id
          ? { ...item, quantity: item.quantity + 1 }
          : item
      )
    );
  };
  
  const decreaseQuantity = (product) => {
    setCart((prevCart) =>
      prevCart
        .map((item) =>
          item.id === product.id && item.quantity > 1
            ? { ...item, quantity: item.quantity - 1 }
            : item
        )
        .filter((item) => item.quantity > 0) // remove if qty=0
    );
  };

    // Remove item from cart
    const removeFromCart = (productId) => {
      setCart(cart.filter((item) => item.id !== productId));
    };

  // ✅ Clear entire cart

  const clearCart = () => setCart([]);

  return (
    <CartContext.Provider value={{ cart, addToCart, updateQuantity,increaseQuantity, decreaseQuantity, clearCart,removeFromCart  }}>
      {children}
    </CartContext.Provider>
  );
};

export default CartProvider;
