// src/components/CartPage.jsx
import React, { useContext, useState } from "react";
import { CartContext } from "../context/CartContext";
import { placeOrder } from "../api";



const CartPage = () => {
  // const { cart, updateQuantity, clearCart } = useContext(CartContext);
  // const [form, setForm] = useState({ firstName: "", lastName: "", address: "" });
  // const [message, setMessage] = useState("");

  // const total = cart.reduce((acc, item) => acc + item.price * item.quantity, 0);

  const { cart, clearCart } = useContext(CartContext);
  const [name, setName] = useState("");
  const [address, setAddress] = useState("");
  const [city, setCity] = useState("");
  const [successMessage, setSuccessMessage] = useState("");









  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  // const placeOrder = () => {
  //   if (!form.firstName || !form.lastName || !form.address) {
  //     setMessage("Please fill all required fields.");
  //     return;
  //   }

  //   // later will call backend API
  //   console.log("Order Placed:", { cart, form });
  //   setMessage("✅ Order placed successfully!");
  //   clearCart();
  // };



  // const placeOrderHandler = async () => {
  //   if (!form.firstName || !form.lastName || !form.address) {
  //     setMessage("Please fill all required fields.");
  //     return;
  //   }
  
  //   try {
  //     const res = await placeOrder({ ...form, cart });
  //     if (res.message) {
  //       setMessage("✅ " + res.message);
  //       clearCart();
  //       // reset form
  //       setForm({ firstName: "", lastName: "", address: "" });
  //     } else {
  //       setMessage("❌ Something went wrong.");
  //     }
  //   } catch (err) {
  //     console.error(err);
  //     setMessage("❌ Failed to place order.");
  //   }
  // };
  
  const handlePlaceOrder = async () => {
    if (!name || !address || !city) {
      alert("Please fill all fields");
      return;
    }

    try {
      await axios.post("http://localhost:5000/orders", {
        name,
        address,
        city,
        cart,
      });

      // ✅ show success message
      setSuccessMessage("✅ Your order has been placed successfully!");

      // ✅ clear cart
      clearCart();

      // Optional: hide message after 3 sec
      setTimeout(() => setSuccessMessage(""), 3000);

    } catch (error) {
      console.error("Order failed", error);
      alert("Order failed. Try again!");
    }
  };




  return (
    <div className="cart">
      <h2>Your Cart</h2>
      {cart.length === 0 ? (
        <p>Cart is empty.</p>
      ) : (
        <>
          <ul>
            {cart.map((item) => (
              <li key={item.id}>
                {item.name} - ₹{item.price} ×
                <input
                  type="number"
                  value={item.quantity}
                  min="1"
                  onChange={(e) => updateQuantity(item.id, e.target.value)}
                />
              </li>
            ))}
          </ul>
          <h3>Total: ₹{total}</h3>
          <div className="form">
            <input
              type="text"
              name="firstName"
              placeholder="First Name"
              value={form.firstName}
              onChange={handleChange}
            />
            <input
              type="text"
              name="lastName"
              placeholder="Last Name"
              value={form.lastName}
              onChange={handleChange}
            />
            <input
              type="text"
              name="address"
              placeholder="Address"
              value={form.address}
              onChange={handleChange}
            />
          </div>
          <button onClick={placeOrderHandler}>Place Order</button>

          {message && <p>{message}</p>}
        </>
      )}
    </div>
  );
};

export default CartPage;
